script:
frappe.call({
    method: 'sarvadhi_hrms.utils.todo.get_upcoming_birthdays', // Backend method to get upcoming birthdays
    callback: function(r) {
        let birthdays_list = r.message; // Assuming the data comes as an array of birthdays
        console.log(birthdays_list, "Upcoming Birthdays");

        let birthday_list_div = root_element.getElementById('birthday_list');
        birthday_list_div.innerHTML = ""; // Clear the list before adding new data

        if (birthdays_list && birthdays_list.length > 0) {
            let birthdayItems = birthdays_list.map(function(birthday) {
                return `<div class="birthday-item">
                    <div class="birthday-info">
                        <div class="employee-name">${birthday.name}</div>
                        <div class="birthday-date">${birthday.date}</div>
                    </div>
                </div>`;
            }).join('');
            birthday_list_div.innerHTML += birthdayItems;
        } else {
            birthday_list_div.innerHTML = '<div class="no-birthday">No upcoming birthdays.</div>';
        }
    },
    error: function(r) {
        console.log("Error fetching birthdays", r);
    }
});

